import React, { useState, useEffect, useRef } from 'react';
import AgoraRTC from "agora-rtc-sdk-ng";
import axios from "axios";

const client = AgoraRTC.createClient({ mode: "live", codec: "vp8" });

export default function App() {
    const [channelName, setChannelName] = useState("channelName");
    const [clientUID, setClientUID] = useState(1234); // The "client" user
    const [agentUID, setAgentUID] = useState(9999);   // The AI agent UID
    const [ispRoleUID, setISPRoleUID] = useState(7777); // The "ISP agent" role-play user
    const [joined, setJoined] = useState(false);
    const [appId, setAppId] = useState("c53554d82fbd4a07b394bcc22ccd2b1e");
    const [loading, setLoading] = useState(false);

    // Client data for negotiation
    const [clientName, setClientName] = useState("Jane Doe");
    const [clientDOB, setClientDOB] = useState("1990-01-01");
    const [clientAddress, setClientAddress] = useState("123 Main St");
    const [clientAccount, setClientAccount] = useState("ACC123456");

    const localAudioTrackRef = useRef(null);

    useEffect(() => {
        // Show OpenAI API key in a popup when the website is accessed
        const openaiApiKey = process.env.REACT_APP_OPENAI_API_KEY;
        if (openaiApiKey) {
            alert(`OpenAI API Key: ${openaiApiKey}`);
        } else {
            alert("OpenAI API Key is not set.");
        }

        // Cleanup on unmount
        return () => {
            if (joined) {
                client.leave();
            }
        };
    }, [joined]);

    const joinChannel = async () => {
        if (!appId) {
            alert("Please set your Agora App ID in the .env file.");
            return;
        }
        setLoading(true);
        try {
            await client.join(appId, channelName, null, clientUID);
            setJoined(true);
            console.log("Joined channel as user", clientUID);
            // Subscribe automatically to remote users
            client.on("user-published", async (user, mediaType) => {
                await client.subscribe(user, mediaType);
                if (mediaType === "audio") {
                    const audioTrack = user.audioTrack;
                    audioTrack.play();
                }
            });
            client.on("user-unpublished", user => {
                console.log("user unpublished:", user.uid);
            });
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const leaveChannel = async () => {
        setLoading(true);
        try {
            await client.leave();
            setJoined(false);
            console.log("Left channel");
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleSpeakUpAsISP = async () => {
        setLoading(true);
        // Create a separate local audio track for the "ISP agent" role
        if (!localAudioTrackRef.current) {
            localAudioTrackRef.current = await AgoraRTC.createMicrophoneAudioTrack();
        }
        // Publish that track with a new UID:
        try {
            await client.join(appId, channelName, null, ispRoleUID);
            await client.publish([localAudioTrackRef.current]);
            console.log("You are now 'ISP agent' on the same channel, UID:", ispRoleUID);
        } catch (err) {
            console.error("ISP speak up error:", err);
        } finally {
            setLoading(false);
        }
    };

    const startAgent = async () => {
        setLoading(true);
        try {
            const resp = await axios.post("http://localhost:8080/start_agent", {
                channel_name: channelName,
                uid: agentUID,
                client_name: clientName,
                client_dob: clientDOB,
                client_address: clientAddress,
                client_account: clientAccount
            });
            console.log(resp.data);
            alert("AI Agent started!");
            alert(`OpenAI API Key: ${process.env.REACT_APP_OPENAI_API_KEY}`);
        } catch (err) {
            console.error("Error details:", err);
            if (!err.response) {
                console.error("Network error details:", err);
                alert("Network error: Please check your connection and try again.");
            } else if (err.response.status === 400) {
                alert("Bad request: " + err.response.data.error);
                if (err.response.data.error.includes("OPENAI_API_KEY")) {
                    console.error("Server missing OPENAI_API_KEY.");
                    alert("Server missing OPENAI_API_KEY.");
                }
            } else if (err.response.status === 500) {
                alert("Server error: " + err.response.data.error);
            } else {
                alert("Failed to start agent: " + err.message);
            }
        } finally {
            setLoading(false);
        }
    };

    const stopAgent = async () => {
        setLoading(true);
        try {
            const resp = await axios.post("http://localhost:8080/stop_agent", {
                channel_name: channelName,
            });
            console.log(resp.data);
            alert("AI Agent stopped.");
        } catch (err) {
            console.error(err);
            alert("Failed to stop agent: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: 20 }}>
            <h1>ISP Negotiation Demo</h1>
            <p>
                This demo app lets you join an Agora voice channel as the <strong>client</strong>.
                An AI agent will also join the channel to negotiate on your behalf using
                OpenAI's Realtime Voice.
            </p>

            <div style={{ marginBottom: 10 }}>
                <label>Client UID: </label>
                <input
                    type="number"
                    value={clientUID}
                    onChange={(e) => setClientUID(Number(e.target.value))}
                />
            </div>

            <div style={{ marginBottom: 10 }}>
                <label>AI Agent UID: </label>
                <input
                    type="number"
                    value={agentUID}
                    onChange={(e) => setAgentUID(Number(e.target.value))}
                />
            </div>

            <h2>Client Data</h2>
            <div style={{ marginBottom: 10 }}>
                <label>Name: </label>
                <input
                    value={clientName}
                    onChange={(e) => setClientName(e.target.value)}
                />
            </div>
            <div style={{ marginBottom: 10 }}>
                <label>DOB: </label>
                <input
                    value={clientDOB}
                    onChange={(e) => setClientDOB(e.target.value)}
                />
            </div>
            <div style={{ marginBottom: 10 }}>
                <label>Address: </label>
                <input
                    value={clientAddress}
                    onChange={(e) => setClientAddress(e.target.value)}
                />
            </div>
            <div style={{ marginBottom: 10 }}>
                <label>Account #: </label>
                <input
                    value={clientAccount}
                    onChange={(e) => setClientAccount(e.target.value)}
                />
            </div>

            {!joined ? (
                <button onClick={joinChannel} disabled={loading}>
                    {loading ? "Joining..." : "Join Channel as Client"}
                </button>
            ) : (
                <button onClick={leaveChannel} disabled={loading}>
                    {loading ? "Leaving..." : "Leave Channel"}
                </button>
            )}

            <hr />

            <button onClick={startAgent} disabled={loading}>
                {loading ? "Starting AI Agent..." : "Start AI Agent"}
            </button>
            <button onClick={stopAgent} disabled={loading}>
                {loading ? "Stopping AI Agent..." : "Stop AI Agent"}
            </button>

            <hr />
            <button onClick={handleSpeakUpAsISP} disabled={loading}>
                {loading ? "Speaking Up..." : "Speak Up as ISP Agent"}
            </button>

            <p style={{ marginTop: 20, fontStyle: "italic" }}>
                **Say "hold the call" to the AI. The AI should politely ask the ISP agent to wait,
                then talk privately to the client. When youâ€™re done, the AI says "Thanks for waiting, let's continue."
            </p>
        </div>
    );
}
